import './App.css';
import { SearchBar } from '../SearchBar/SearchBar';
import { SearchResults } from '../SearchResults/SearchResults';
import { Playlist } from '../Playlist/Playlist';
import React from 'react';
import Spotify from '../../util/Spotify';

export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { searchResults: [
        {
          name: "",
          artist: "",
          album: "",
          id: ""
        }],

      playlistName: "",

      playlistTracks: [
        {
          name: "",
          artist: "",
          album: "",
          id: ""
        }]
    };
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.updatePlaylistName = this.updatePlaylistName.bind(this);
    this.savePlaylist = this.savePlaylist.bind(this);
    this.search = this.search.bind(this);
  }


  addTrack(track) {
    if (this.state.playlistTracks.find(savedTrack => savedTrack.id  === track.id)) {
      return;
    }
    this.setState(prev => ({
      playlistTracks: [...prev, track]
    }));

  }

  removeTrack(track) {
    this.setState(prev => ({
      playlistTracks: prev.filter(t => t.id !== track.id)
    }));
  }

  updatePlaylistName(name) {
    this.setState({ playlistName: name });
  }

  savePlaylist() {
    let trackURIs = [];
    Spotify.savePlaylist(this.state.playlistName, trackURIs);
    this.setState({ playlistName: "" });
    this.setState({ playlistTracks: [] });
  }

  search(term) {
    console.log(term);
  }


  render() {
      return (

        <div>
            <h1>Ja<span className="highlight">mmm</span>ing</h1>
            <div className="App">
                {/* <!-- Add a SearchBar component --> */}
                <SearchBar onSearch={this.search} />
                <div className="App-playlist">
                    {/* <!-- Add a SearchResults component --> */}
                    <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack} />
                    {/* <!-- Add a Playlist component --> */}
                    <Playlist playlistTracks={this.state.playlistTracks} playlistName={this.state.playlistName} onRemove={this.removeTrack} onNameChange={this.updatePlaylistName} onSave={this.savePlaylist} />
                </div>
            </div>
        </div>
      );
  }
}

export default App;
